// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

contract CertificateRegistry is Ownable, ReentrancyGuard {
    using ECDSA for bytes32;

    struct Certificate {
        string recipientName;
        string courseName;
        uint256 completionDate;
        address issuerAddress;
        uint256 issueDate;
        bool isValid;
        bool isRevoked;
        uint256 revocationDate;
        bytes signature;
    }

    mapping(bytes32 => Certificate) public certificates;
    mapping(address => bool) public authorizedIssuers;
    event CertificateIssued(bytes32 indexed certificateId, string recipientName, string courseName);
    event CertificateRevoked(bytes32 indexed certificateId);
    event IssuerAuthorized(address indexed issuer);
    event IssuerDeauthorized(address indexed issuer);

    constructor() {
        authorizedIssuers[msg.sender] = true;
    }

    modifier onlyAuthorizedIssuer() {
        require(authorizedIssuers[msg.sender], "Not an authorized issuer");
        _;
    }

    function authorizeIssuer(address issuer) external onlyOwner {
        authorizedIssuers[issuer] = true;
        emit IssuerAuthorized(issuer);
    }

    function deauthorizeIssuer(address issuer) external onlyOwner {
        authorizedIssuers[issuer] = false;
        emit IssuerDeauthorized(issuer);
    }

    function issueCertificate(
        string memory recipientName,
        string memory courseName,
        uint256 completionDate,
        bytes memory signature
    ) external onlyAuthorizedIssuer nonReentrant {
        bytes32 certificateId = keccak256(
            abi.encodePacked(
                recipientName,
                courseName,
                completionDate,
                msg.sender,
                block.timestamp
            )
        );

        require(!certificates[certificateId].isValid, "Certificate already exists");

        certificates[certificateId] = Certificate({
            recipientName: recipientName,
            courseName: courseName,
            completionDate: completionDate,
            issuerAddress: msg.sender,
            issueDate: block.timestamp,
            isValid: true,
            isRevoked: false,
            revocationDate: 0,
            signature: signature
        });

        emit CertificateIssued(certificateId, recipientName, courseName);
    }

    function revokeCertificate(bytes32 certificateId) external onlyAuthorizedIssuer {
        require(certificates[certificateId].isValid, "Certificate does not exist");
        require(!certificates[certificateId].isRevoked, "Certificate already revoked");

        certificates[certificateId].isRevoked = true;
        certificates[certificateId].isValid = false;
        certificates[certificateId].revocationDate = block.timestamp;

        emit CertificateRevoked(certificateId);
    }

    function verifyCertificate(bytes32 certificateId) external view returns (bool) {
        Certificate memory cert = certificates[certificateId];
        return cert.isValid && !cert.isRevoked;
    }

    function getCertificate(bytes32 certificateId) external view returns (
        string memory recipientName,
        string memory courseName,
        uint256 completionDate,
        address issuerAddress,
        uint256 issueDate,
        bool isValid,
        bool isRevoked,
        uint256 revocationDate
    ) {
        Certificate memory cert = certificates[certificateId];
        return (
            cert.recipientName,
            cert.courseName,
            cert.completionDate,
            cert.issuerAddress,
            cert.issueDate,
            cert.isValid,
            cert.isRevoked,
            cert.revocationDate
        );
    }

    function verifySignature(
        bytes32 certificateId,
        bytes memory signature
    ) external view returns (bool) {
        Certificate memory cert = certificates[certificateId];
        bytes32 messageHash = keccak256(
            abi.encodePacked(
                cert.recipientName,
                cert.courseName,
                cert.completionDate,
                cert.issuerAddress,
                cert.issueDate
            )
        );
        bytes32 ethSignedMessageHash = messageHash.toEthSignedMessageHash();
        address signer = ethSignedMessageHash.recover(signature);
        return signer == cert.issuerAddress;
    }
}
